import { Component, OnInit } from '@angular/core';
import { MatDialogRef } from '@angular/material/dialog';
import { PublicacionesService } from 'src/app/services/admin/publicaciones.service';
import { Publicacion } from 'src/app/Models/Publicacion';

//declare var jsPDF: any;

import * as jsPDF from 'jspdf';
import 'jspdf-autotable';
import { SalidasService } from 'src/app/services/admin/salidas.service';
import { StocksService } from 'src/app/services/admin/stocks.service';
import { VentasService } from 'src/app/services/admin/ventas.service';
import { Venta } from 'src/app/Models/Venta';
import { Stock } from 'src/app/Models/Stock';
import { Salida } from 'src/app/Models/Salida';
import { MaterialesService } from 'src/app/services/admin/materiales.service';
import { EntradasService } from 'src/app/services/admin/entradas.service';
import { EntregasService } from 'src/app/services/admin/entregas.service';
import { Material } from 'src/app/Models/Material';
import { Entrada } from 'src/app/Models/Entrada';
import { Entrega } from 'src/app/Models/Entrega';

//var jsPDF = require('jspdf');
//require('jspdf-autotable');

@Component({
  selector: 'app-reportes',
  templateUrl: './reportes.component.html',
  styleUrls: ['./reportes.component.css']
})
export class ReportesComponent implements OnInit {


  logoUneg:string;

  tipo_reporte:any=null;
  inicio:string;
  fin:string;

  constructor(
    public dialogRef: MatDialogRef<ReportesComponent>,
    public publicacionesService:PublicacionesService,
    public salidasService:SalidasService,
    public stockService:StocksService,
    public ventasService:VentasService,
    public materialesService:MaterialesService,
    public entradasService:EntradasService,
    public entregasService:EntregasService,
  ) {
    this.logoUneg= 'data:image/jpeg;base64,';

  }

  

  publicaciones :any =[];

  publicacionesColumns = [
    {title: "ISBN", dataKey: "isbn"},
    {title: "Título", dataKey: "titulo"},
    {title: "Publicación", dataKey: "tipo_publicacion"}, 
    {title: "Impreso", dataKey: "cantidad_impresa"},
  ];

  ventas :any =[];

  ventaColumns = [
    {title: "Recibo", dataKey: "bauche"},
    {title: "Banco", dataKey: "banco"},
    {title: "Cantidad", dataKey: "cantidad"},
    {title: "Monto Debito", dataKey: "monto_debito"},
    {title: "Fecha", dataKey: "created_at"},
  ];

  salidas:any=[];

  salidasColumns = [
    {title: "Cliente", dataKey: "cliente"},
    {title: "Sede", dataKey: "sede"},
    {title: "Entrega", dataKey: "tipo_entrega"},
    {title: "Cantidad", dataKey: "cantidad"},
    {title: "Fecha", dataKey: "created_at"}, 
  ];

  stock:any=[];

  stockColumns = [
    {title: "Publicacion", dataKey: "titulo"},
    {title: "Tipo", dataKey: "tipo_cantidad"},
    {title: "Entrega", dataKey: "tipo_entrega"}, 
    {title: "Cantidad", dataKey: "cantidad"},
    {title: "Fecha", dataKey: "created_at"}, 
  ];

  materiales:any=[];

  materialesColumns = [
    {title: "Codigo", dataKey: "codigo"},
    {title: "Nombre", dataKey: "nombre"}, 
    {title: "Cantidad", dataKey: "cantidad"},
    {title: "Fecha", dataKey: "created_at"}, 
  ];


  materialesEntradas:any=[];

  materialesEntradasColumns = [
    {title: "Codigo", dataKey: "codigo"},
    {title: "Nombre", dataKey: "nombre"}, 
    {title: "Cantidad", dataKey: "cantidad"},
    {title: "Fecha", dataKey: "created_at"}, 
  ];

  materialesSalidas:any=[];

  materialesSalidasColumns = [
    {title: "Nombre", dataKey: "nombre"},
    {title: "Asignado a", dataKey: "persona"},
    {title: "Cantidad", dataKey: "cantidad"},
    {title: "Proyecto", dataKey: "proyecto"},
    {title: "Fecha", dataKey: "created_at"}, 
  ];




  ngOnInit() {
  }

  updatePublicacionesList(){
    this.publicacionesService.getPublicaciones().subscribe(
      data => {
        this.publicaciones = data as Publicacion[];
        console.log(this.publicaciones);
        this.PDFPublicaciones();
      }
    );
  }

  updateVentasList(){
    this.ventasService.getVentasReporte().subscribe(
      data=>{
        console.log(data);
        this.ventas = data as Venta[];
        this.PDFVentas();
      }
    );
  }

  updateStockList(){
    this.stockService.getStocksReporte().subscribe(
      data => {
        this.stock = data as Stock[];
        console.log(data);
        this.PDFStock();
      }
    );
  }

  updateSalidasList(){
    this.salidasService.getSalidasReporte().subscribe(
      data=>{
        console.log(data);
        this.salidas = data as Salida[];
        this.PDFSalidas();
      }
    );
  }

  //---------------------------------------

  updateMaterialesList(){
    this.materialesService.getMaterialesList().subscribe(
      data => {
        this.materiales = data as Material[];
        this.PDFMateriales();
      }
    );
  }

  updateEntradasList(){
    this.entradasService.getEntradasReporte().subscribe(
      res=>{

        this.materialesEntradas = res as Entrada[];
        this.PDFMaterialesEntradas();
        //console.log("dentro response entradas",this.entradasService.entradas);
      }
    );
  }

  updateEntregasList(){
    this.entregasService.getEntregasReporte().subscribe(
      res=>{
        this.materialesSalidas = res as Entrega[];
        this.PDFMaterialesSalidas();
        //console.log("dentro response entregas",this.entregasService.entregas);
      }
    );
  }



  generarPDF(){

    if(this.tipo_reporte=='Publicaciones disponibles'){
      this.updatePublicacionesList();
    }else

    if(this.tipo_reporte=='Entradas de publicaciones'){
      this.updateStockList();
    }else

    if(this.tipo_reporte=='Salidas de publicaciones'){
      this.updateSalidasList();
    } else 

    if(this.tipo_reporte=='Ventas de publicaciones'){
      this.updateVentasList();
    }

    if(this.tipo_reporte=='Materiales disponibles'){
      this.updateMaterialesList();
    }

    if(this.tipo_reporte=='Entradas de materiales'){
      this.updateEntradasList();
    }

    if(this.tipo_reporte=='Salidas de materiales'){
      this.updateEntregasList();
    }


  }

  PDFPublicaciones(){
    var doc = new jsPDF('p', 'pt');
    
    doc.autoTable(this.publicacionesColumns, this.publicaciones, {
      margin: {top: 145},
      addPageContent: function() {
        doc.setFontSize(12);
        doc.text("Universidad Nacional Experimental de Guayana", 170, 55);
        doc.text("Secretaría", 265, 75);
        doc.text("Fondo Editorial", 255, 95);
        doc.text("Reporte de Publicaciones:", 40, 125);
      }
    });
    doc.addImage(this.logoUneg, 'JPEG', 65, 40, 40, 40);
    doc.save('Publicaciones.pdf');

  }

  PDFVentas(){
    var doc = new jsPDF('p', 'pt');
    
    doc.autoTable(this.ventaColumns, this.ventas, {
      margin: {top: 145},
      addPageContent: function() {
        doc.setFontSize(12);
        doc.text("Universidad Nacional Experimental de Guayana", 170, 55);
        doc.text("Secretaría", 265, 75);
        doc.text("Fondo Editorial", 255, 95);
        doc.text("Reporte de Ventas de publicaciones:", 40, 125);
      }
    });
    doc.addImage(this.logoUneg, 'JPEG', 65, 40, 40, 40);
    doc.save('Ventas.pdf');

  }

  PDFSalidas(){
    var doc = new jsPDF('p', 'pt');
    
    doc.autoTable(this.salidasColumns, this.salidas, {
      margin: {top: 145},
      addPageContent: function() {
        doc.setFontSize(12);
        doc.text("Universidad Nacional Experimental de Guayana", 170, 55);
        doc.text("Secretaría", 265, 75);
        doc.text("Fondo Editorial", 255, 95);
        doc.text("Reporte de Salidas de publicaciones:", 40, 125);
      }
    });
    doc.addImage(this.logoUneg, 'JPEG', 65, 40, 40, 40);
    doc.save('Salidas.pdf');

  }

  PDFStock(){
    var doc = new jsPDF('p', 'pt');
    
    doc.autoTable(this.stockColumns, this.stock, {
      margin: {top: 145},
      addPageContent: function() {
        doc.setFontSize(12);
        doc.text("Universidad Nacional Experimental de Guayana", 170, 55);
        doc.text("Secretaría", 265, 75);
        doc.text("Fondo Editorial", 255, 95);
        doc.text("Reporte de entradas de publicaciones:", 40, 125);
      }
    });
    doc.addImage(this.logoUneg, 'JPEG', 65, 40, 40, 40);
    doc.save('Entradas.pdf');

  }

  PDFMateriales(){
    var doc = new jsPDF('p', 'pt');
    
    doc.autoTable(this.materialesColumns, this.materiales, {
      margin: {top: 145},
      addPageContent: function() {
        doc.setFontSize(12);
        doc.text("Universidad Nacional Experimental de Guayana", 170, 55);
        doc.text("Secretaría", 265, 75);
        doc.text("Fondo Editorial", 255, 95);
        doc.text("Reporte de materiales:", 40, 125);
      }
    });
    doc.addImage(this.logoUneg, 'JPEG', 65, 40, 40, 40);
    doc.save('Materiales.pdf');

  }

  PDFMaterialesEntradas(){
    var doc = new jsPDF('p', 'pt');
    
    doc.autoTable(this.materialesEntradasColumns, this.materialesEntradas, {
      margin: {top: 145},
      addPageContent: function() {
        doc.setFontSize(12);
        doc.text("Universidad Nacional Experimental de Guayana", 170, 55);
        doc.text("Secretaría", 265, 75);
        doc.text("Fondo Editorial", 255, 95);
        doc.text("Reporte de entrada de materiales:", 40, 125);
      }
    });
    doc.addImage(this.logoUneg, 'JPEG', 65, 40, 40, 40);
    doc.save('Entrada de Materiales.pdf');

  }

  PDFMaterialesSalidas(){
    var doc = new jsPDF('p', 'pt');
    
    doc.autoTable(this.materialesSalidasColumns, this.materialesSalidas, {
      margin: {top: 145},
      addPageContent: function() {
        doc.setFontSize(12);
        doc.text("Universidad Nacional Experimental de Guayana", 170, 55);
        doc.text("Secretaría", 265, 75);
        doc.text("Fondo Editorial", 255, 95);
        doc.text("Reporte de Salidas de materiales:", 40, 125);
      }
    });
    doc.addImage(this.logoUneg, 'JPEG', 65, 40, 40, 40);
    doc.save('Salidas de Materiales.pdf');

  }

}
